import React, { Component } from 'react'
import './App.css';
import allshows from './shows'
import allepisodes from './episodes'
import Shows from './components/Shows'
import Header from './components/Header';
import MainPage from './components/MainPage';



export default class App extends Component {

state={
  shows:allshows,
  episodes:[]
} 

selectShow=(e)=>{
  console.log(e.target.value)
}

goToEpisode=(id)=>{
  this.setState({
    shows:this.state.shows.filter(show=>show.id===id)
  })
}



  render() {
    return (
      <div>

        <Header />
        <br/>
        <Shows shows={this.state.shows}  selectShow={this.selectShow}/>
        <MainPage shows={this.state.shows} selectShow={this.selectShow} goToEpisode={this.goToEpisode}/>
      </div>
    )
  }
}
