import React, { Component } from 'react'

export default class Main extends Component {
    render() {
        return (
            <div style={{backgroundColor:"white", border:'solid 1px grey',margin:'5px',display: "flex",
            justifyContent: "space-around",
            flexWrap: "wrap" }}> 

                  <div  style={{ width: '10rem'}} >
                                  <img width="100%" src="http://static.tvmaze.com/uploads/images/medium_portrait/9/22757.jpg" alt=""/>
                                  <div style={{flex: 1}}>
                                      <h2>{this.props.show.name}</h2>
                                    <p>
                                    {this.props.show.summary.replace(/<[^>]+>/g, '')}  
                                    </p>  

                                  </div>
                 </div>

            </div>
        )
    }
}
