import React, { Component } from 'react'
import SingleShow from './SingleShow'


export default class Shows extends Component {
    render() {
        return (

            <div >

               <form className='form' style={{border:'solid red'}} >
                    <select>
                        <option>Select a show...</option>
                        {this.props.shows.map(show=> <SingleShow key={show.id} show={show} selectShow={this.props.selectShow}/>  )}
                    </select>

                    <select>
                        <option>Select an episode...</option>


                    </select>
                    <input placeholder="Search episode..."></input>
               </form>

            </div>



        )
    }
}
