import React, { Component } from 'react'

export default class OneShow2 extends Component {
    render() {
        return (
 <div style={{backgroundColor:"white", border:'solid 1px grey',margin:'5px',borderRadius:"5%"}} > 


   <div>
                  <div  style={{ width: '20rem',height:'550px',padding:"10px",float:"left"}} >

                  {this.props.show.image !== null ? <img width="100%" src={this.props.show.image.original} alt=" " style={{borderRadius:"5%"}}/> : <img width="100%" src="https://i.picsum.photos/id/563/200/300.jpg?hmac=SZO4DbUo3eM7wbKdFWm2jUkpnLD7OkJda_aTTd8FP9k" alt=" " style={{borderRadius:"15%"}}/> }



                 </div>
                 <div style={{width:"550px", float:"right"}}>
                 <h2>{this.props.show.name}</h2>
                                      <h4>Genres:</h4>
                                      <p> {this.props.show.genres.map(item=>`${item} `)} </p>  

                                      <h4>Language:</h4>
                                      <p>{this.props.show.language}</p>
                                      <h4>Summary:</h4>
                                      <p>{this.props.show.summary.replace(/<[^>]+>/g, '')}</p>
                                     <a target="_blank" href={this.props.show.url}><button style={{backgroundColor:"#D7F7F7",padding:"10px",fontSize:"30px",borderRadius:"5%"}}>Watch</button></a> 

                 </div>

         </div>
                 <div>
                     {this.props.episodes.map(item=>{
                        return item.name
                     })}
                 </div>


            </div>
        ) 
    }
}
