import React, { Component } from 'react'
import header from './header.jpeg'

export default class Header extends Component {
    render() {
        return (
            <div style={{backgroundColor:"pink",height:"150px"}}>

                <form>
               <select>
                   <option>Select a show...</option>
                   {this.props.shows.map(item=>
                    <option onChange={this.props.selectShow}>{item.name}</option>)}
               </select>

               <select>
                   <option>Select an episode...</option>

               </select>
               <input placeholder="Search episode..."></input>
               </form>

            </div>
        )
    }
}
